(16, 'designtrail02', '                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             <p><span style=\"color: rgb(51, 51, 51); font-family: Roboto, sans-serif; font-size: 32px; font-weight: 700;\">How to learn  </span><br></p><p><b>                                          </b></p>                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                      ', 1, '10', 2),
